from collections import Counter


def longestPalindrome(s):
    """
    :type s: str
    :rtype: int
    """
    length = 0
    chars = Counter(list(s))
    getOddWhole = False
    for letter, repeat in chars.items():
        if repeat % 2 == 0:
            length += repeat
        else:
            if getOddWhole:
                length += repeat - 1
            else:
                length += repeat
                getOddWhole = True
    return length


print(longestPalindrome(
    '""'))
